import{e}from"./app.a770902d.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";var o="/school/assets/udp-1.dfcc3282.png",c="/school/assets/udp-2.8d58984c.png";const p={},P=e('<h1 id="\u7528\u6237\u6570\u636E\u62A5\u534F\u8BAE-udp" tabindex="-1"><a class="header-anchor" href="#\u7528\u6237\u6570\u636E\u62A5\u534F\u8BAE-udp" aria-hidden="true">#</a> \u7528\u6237\u6570\u636E\u62A5\u534F\u8BAE\uFF08UDP\uFF09</h1><h2 id="\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u6982\u8FF0" aria-hidden="true">#</a> \u6982\u8FF0</h2><p>\u7528\u6237\u6570\u636E\u62A5\u534F\u8BAE<code>UDP</code>\u53EA\u5728\u7F51\u9645\u534F\u8BAEIP\u7684\u57FA\u7840\u4E0A\u589E\u52A0\u4E86\u590D\u7528/\u5206\u7528\u529F\u80FD\u548C\u5DEE\u9519\u68C0\u6D4B\u529F\u80FD\uFF0C\u4E3A\u7F51\u7EDC\u7528\u6237\u63D0\u4F9B\u9AD8\u6548\u7387\u7684\u6570\u636E\u4F20\u8F93\u670D\u52A1\u3002</p><p><code>UDP</code>\u5177\u6709\u4EE5\u4E0B\u4E3B\u8981\u7279\u70B9\uFF1A</p><p>\u2460 <code>UDP</code>\u662F\u65E0\u8FDE\u63A5\u7684\uFF0C\u6CA1\u6709\u5EFA\u7ACB\u8FDE\u63A5\u548C\u91CA\u653E\u8FDE\u63A5\u7684\u8FC7\u7A0B\u3002</p><p>\u2461 <code>UDP</code>\u53EA\u80FD\u5C3D\u6700\u5927\u52AA\u529B\u4EA4\u4ED8\uFF0C\u63D0\u4F9B\u4E0D\u53EF\u9760\u7684\u4F20\u8F93\u670D\u52A1\u3002\u5BF9\u4E8E\u51FA\u73B0\u5DEE\u9519\u7684\u62A5\u6587\u8FDB\u884C\u4E22\u5F03\u5904\u7406\u3002</p><p>\u2462 <code>UDP</code>\u662F\u9762\u5411\u62A5\u6587\u7684\uFF0C\u4E0E\u5E94\u7528\u5C42\u4EA4\u4ED8\u7684\u662F\u5B8C\u6574\u7684\u62A5\u6587\uFF0C\u65E2\u4E0D\u5408\u5E76\uFF0C\u4E5F\u4E0D\u62C6\u5206\uFF0C\u4FDD\u7559\u539F\u59CB\u62A5\u6587\u7684\u8FB9\u754C\u3002\u56E0\u6B64\uFF0C\u5E94\u7528\u5C42\u5FC5\u987B\u9009\u62E9\u5408\u9002\u7684\u62A5\u6587\u957F\u5EA6\uFF0C\u4EE5\u514D\u5728IP\u5C42\u8FDB\u884C\u5206\u7247\u5DE5\u4F5C\uFF0C\u4ECE\u800C\u964D\u4F4EIP\u5C42\u7684\u6548\u7387\u3002</p><p>\u2463 <code>UDP</code>\u6CA1\u6709\u62E5\u585E\u63A7\u5236\u7684\u529F\u80FD\u3002\u5373\u4F7F\u7F51\u7EDC\u51FA\u73B0\u62E5\u585E\u4E5F\u4E0D\u4F1A\u4F7F\u6E90\u4E3B\u673A\u964D\u4F4E\u53D1\u9001\u901F\u7387\uFF0C\u5B81\u613F\u4E22\u5931\u6570\u636E\uFF0C\u4E5F\u4E0D\u5141\u8BB8\u4F20\u9001\u7684\u6570\u636E\u5B58\u5728\u8F83\u5927\u7684\u65F6\u5EF6\uFF0C\u8FD9\u6B63\u9002\u5408\u5B9E\u65F6\u5E94\u7528\uFF08\u5982IP\u7535\u8BDD\u3001\u5B9E\u65F6\u89C6\u9891\u4F1A\u8BAE\uFF09\u7684\u9700\u8981\u3002</p><p>\u2464 <code>UDP</code>\u9996\u90E8\u7B80\u77ED\uFF0C\u53EA\u67098\u5B57\u8282\uFF0C\u51CF\u5C11\u4E86\u901A\u4FE1\u5F00\u9500\u3002</p><p>\u867D\u7136\u67D0\u4E9B\u5E94\u7528\u9700\u8981\u4F7F\u7528\u6CA1\u6709\u62E5\u585E\u63A7\u5236\u7684<code>UDP</code>\uFF0C\u4F46\u5BF9\u4E8E\u67D0\u4E9B\u5B9E\u65F6\u5E94\u7528\uFF08\u5982\u9AD8\u901F\u5B9E\u65F6\u89C6\u9891\u6D41\uFF09\uFF0C\u56E0\u4F7F\u7528\u65E0\u62E5\u585E\u529F\u80FD\u7684<code>UDP</code>\u53EF\u80FD\u4F1A\u5F15\u8D77\u7F51\u7EDC\u7684\u4E25\u91CD\u62E5\u585E\uFF0C\u800C\u4F7F\u7528\u6237\u65E0\u6CD5\u6B63\u5E38\u63A5\u6536\u3002\u53E6\u5916\uFF0C\u8FD8\u6709\u4E00\u4E9B\u5B9E\u65F6\u5E94\u7528\u8981\u6C42\u51CF\u5C11\u6570\u636E\u7684\u4E22\u5931\uFF0C\u8FD9\u5C31\u9700\u8981\u5BF9<code>UDP</code>\u7684\u4E0D\u53EF\u9760\u7684\u4F20\u8F93\u8FDB\u884C\u9002\u5F53\u7684\u6539\u8FDB\uFF0C\u5982\u91C7\u7528\u524D\u5411\u7EA0\u9519\u6216\u91CD\u4F20\u7B49\u63AA\u65BD\u3002</p><p><code>UDP</code>\u534F\u8BAE\u9002\u7528\u4E8E\u53D1\u9001\u77ED\u62A5\u6587\u53C8\u4E0D\u5173\u5FC3\u53EF\u9760\u6027\u7684\u573A\u5408\u3002\u4F8B\u5982\uFF0C\u5728\u5BA2\u6237/\u670D\u52A1\u5668\u60C5\u51B5\u4E0B\uFF0C\u5BA2\u6237\u5411\u670D\u52A1\u5668\u53D1\u9001\u4E00\u4E2A\u77ED\u7684\u8BF7\u6C42\uFF0C\u5E76\u671F\u671B\u5F97\u5230\u4E00\u4E2A\u77ED\u7684\u5E94\u7B54\u3002\u5982\u8BF7\u6C42\u6216\u5E94\u7B54\u4E22\u5931\u7684\u8BDD\uFF0C\u5BA2\u6237\u4F1A\u56E0\u8D85\u65F6\u800C\u91CD\u4F20\uFF0C\u5176\u5F00\u9500\u5C31\u6BD4\u5EFA\u7ACB\u548C\u91CA\u653E\u8FDE\u63A5\u7684\u5F00\u9500\u8981\u5C0F\u5F97\u591A\u3002\u5E94\u7528\u5C42\u7684<code>DNS</code>\u3001<code>RIP</code>\u3001<code>SNMP</code>\u7B49\u534F\u8BAE\u90FD\u4F7F\u7528<code>UDP</code>\u3002<code>UDP</code>\u4E5F\u9002\u7528\u4E8E\u5177\u6709\u6D41\u91CF\u63A7\u5236\u548C\u5DEE\u9519\u63A7\u5236\u673A\u5236\u7684\u5E94\u7528\u5C42\u534F\u8BAE\uFF0C\u5982\u7B80\u5355\u6587\u4EF6\u4F20\u9001\u534F\u8BAE<code>TFTP</code>\u3002</p><h2 id="\u62A5\u6587\u7684\u683C\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u62A5\u6587\u7684\u683C\u5F0F" aria-hidden="true">#</a> \u62A5\u6587\u7684\u683C\u5F0F</h2><p><code>UDP</code>\u62A5\u6587\u53C8\u79F0\u7528\u6237\u6570\u636E\u62A5\uFF0C\u7531\u9996\u90E8\u548C\u6570\u636E\u5B57\u6BB5\u4E24\u90E8\u5206\u7EC4\u6210\u3002\u5176\u683C\u5F0F\u5982\u4E0B\u56FE\u6240\u793A\u3002</p><p><img src="'+o+'" alt="image"></p><p>\u9996\u90E8\u5B57\u6BB5\u75314\u4E2A\u5B57\u6BB5\u7EC4\u6210\uFF0C\u51718\u5B57\u8282\u3002\u5404\u5B57\u6BB5\u7684\u542B\u4E49\u5982\u4E0B\uFF1A</p><ul><li>\uFF081\uFF09<strong>\u6E90\u7AEF\u53E3\uFF0816\u4F4D\uFF09</strong>\u3002\u8FD9\u662F\u6E90\u4E3B\u673A\u4E0A\u8FD0\u884C\u7684\u8FDB\u7A0B\u4F7F\u7528\u7684\u7AEF\u53E3\u53F7\u3002\u5728\u591A\u6570\u60C5\u51B5\u4E0B\uFF0C\u82E5\u6E90\u4E3B\u673A\u662F\u5BA2\u6237\u7AEF\uFF08\u5373\u5BA2\u6237\u8FDB\u7A0B\u53D1\u9001\u8BF7\u6C42\u65F6\uFF09\uFF0C\u8FD9\u4E2A\u7AEF\u53E3\u5C31\u662F\u4E34\u65F6\u7AEF\u53E3\u53F7\uFF1B\u82E5\u6E90\u4E3B\u673A\u662F\u670D\u52A1\u5668\uFF08\u5F53\u670D\u52A1\u5668\u8FDB\u7A0B\u53D1\u9001\u54CD\u5E94\u65F6\uFF09\uFF0C\u8FD9\u4E2A\u7AEF\u53E3\u53F7\u5C31\u662F\u719F\u77E5\u7AEF\u53E3\u53F7\u3002</li><li>\uFF082\uFF09<strong>\u76EE\u7684\u7AEF\u53E3\uFF0816\u4F4D\uFF09</strong>\u3002\u8FD9\u662F\u76EE\u7684\u4E3B\u673A\u4E0A\u8FD0\u884C\u7684\u8FDB\u7A0B\u4F7F\u7528\u7684\u7AEF\u53E3\u53F7\u3002\u5728\u591A\u6570\u60C5\u51B5\u4E0B\uFF0C\u82E5\u76EE\u7684\u4E3B\u673A\u662F\u670D\u52A1\u5668\u7AEF\uFF08\u5F53\u5BA2\u6237\u8FDB\u7A0B\u53D1\u9001\u8BF7\u6C42\u65F6\uFF09\uFF0C\u8FD9\u4E2A\u7AEF\u53E3\u53F7\u662F\u719F\u77E5\u7AEF\u53E3\u53F7\uFF1B\u82E5\u76EE\u7684\u4E3B\u673A\u662F\u5BA2\u6237\u7AEF\uFF08\u5F53\u670D\u52A1\u5668\u8FDB\u7A0B\u53D1\u9001\u54CD\u5E94\u65F6\uFF09\uFF0C\u8FD9\u4E2A\u7AEF\u53E3\u53F7\u662F\u4E34\u65F6\u7AEF\u53E3\u53F7\u3002</li><li>\uFF083\uFF09<strong>\u957F\u5EA6\uFF0816\u4F4D\uFF09</strong>\u3002\u6307\u660E\u5305\u62EC\u9996\u90E8\u5728\u5185\u7684<code>UDP</code>\u62A5\u6587\u7684\u603B\u957F\u5EA6\uFF08\u5305\u62EC\u9996\u90E8\u548C\u6570\u636E\uFF0C\u4EE5\u5B57\u8282\u4E3A\u5355\u4F4D\uFF09\uFF0C\u5176\u6700\u5C0F\u503C\u4E3A8\u3002\u8BE5\u5B57\u6BB5\u7684\u4F5C\u7528\u53EA\u662F\u4FBF\u4E8E\u76EE\u7684\u7AEF\u7684<code>UDP</code>\u4ECE\u7528\u6237\u6570\u636E\u62A5\u63D0\u4F9B\u7684\u4FE1\u606F\u4E2D\u8BA1\u7B97\u51FA\u6570\u636E\u957F\u5EA6\u3002</li><li>\uFF084\uFF09<strong>\u68C0\u9A8C\u548C\uFF0816\u4F4D\uFF09</strong>\u3002\u7528\u4E8E\u68C0\u9A8C<code>UDP</code>\u62A5\u6587\u5728\u4F20\u8F93\u4E2D\u662F\u5426\u5B58\u5728\u5DEE\u9519\u3002\u68C0\u9A8C\u548C\u7684\u68C0\u9A8C\u8303\u56F4\u662F\u6574\u4E2A<code>UDP</code>\u62A5\u6587\uFF08\u5305\u62EC\u9996\u90E8\u548C\u6570\u636E\uFF09\u3002\u5FC5\u987B\u6307\u51FA\uFF0C<code>UDP</code>\u68C0\u9A8C\u548C\u7684\u8BA1\u7B97\u4E0EIP\u548CICMP\u68C0\u9A8C\u548C\u7684\u8BA1\u7B97\u4E0D\u540C\u3002\u8FD9\u91CC\u7684\u68C0\u9A8C\u548C\u5305\u62EC3\u90E8\u5206\uFF1A\u4F2A\u9996\u90E8\u3001<code>UDP</code>\u9996\u90E8\u4EE5\u53CA\u6765\u81EA\u5E94\u7528\u5C42\u7684\u6570\u636E\u3002\u5728\u8BA1\u7B97\u68C0\u9A8C\u548C\u65F6\uFF0C\u5728<code>UDP</code>\u62A5\u6587\u4E4B\u524D\u8981\u589E\u52A0\u4E00\u4E2A\u4F2A\u9996\u90E8\uFF0812\u5B57\u8282\uFF09\uFF0C\u5B83\u4F5C\u4E3AIP\u5206\u7EC4\u9996\u90E8\u7684\u4E00\u90E8\u5206\u3002\u4F2A\u9996\u90E8\u67095\u4E2A\u5B57\u6BB5\uFF1A\u524D\u4E24\u4E2A\u5B57\u6BB5\u5206\u522B\u4E3A\u6E90IP\u5730\u5740\uFF084\u5B57\u8282\uFF09\u548C\u76EE\u7684IP\u5730\u5740\uFF084\u5B57\u8282\uFF09\uFF0C\u7B2C3\u5B57\u6BB5\uFF081\u5B57\u8282\uFF09\u4E3A\u5168\u96F6\uFF0C\u7B2C4\u5B57\u6BB5\uFF081\u5B57\u8282\uFF09\u4E3A\u534F\u8BAE\u503C\uFF08<code>UDP</code>=17\uFF09\uFF0C\u7B2C5\u5B57\u6BB5\uFF082\u5B57\u8282\uFF09\u662F<code>UDP</code>\u62A5\u6587\u957F\u5EA6\u3002\u6240\u8C13\u201C\u4F2A\u9996\u90E8\u201D\u662F\u56E0\u4E3A\u5B83\u5E76\u4E0D\u662F<code>UDP</code>\u62A5\u6587\u7684\u771F\u6B63\u9996\u90E8\uFF0C\u5B83\u53EA\u662F\u5728\u8BA1\u7B97\u68C0\u9A8C\u548C\u65F6\uFF0C\u4E34\u65F6\u6027\u5730\u4E0E<code>UDP</code>\u62A5\u6587\u62FC\u63A5\u5728\u4E00\u8D77\uFF0C\u6784\u6210\u4E00\u4E2A\u4E34\u65F6\u6027\u7684<code>UDP</code>\u62A5\u6587\u3002</li></ul><p><code>UDP</code>\u68C0\u9A8C\u548C\u7684\u5177\u4F53\u8BA1\u7B97\u65B9\u6CD5\u4E0E\u8BA1\u7B97IP\u6570\u636E\u62A5\u9996\u90E8\u68C0\u9A8C\u548C\u7684\u65B9\u6CD5\u76F8\u4F3C\u3002\u6240\u4E0D\u540C\u7684\u662F<code>UDP</code>\u7684\u68C0\u9A8C\u548C\u662F\u628A\u9996\u90E8\u548C\u6570\u636E\u90E8\u5206\u4E00\u8D77\u68C0\u9A8C\u7684\u3002\u68C0\u9A8C\u548C\u7684\u5177\u4F53\u8BA1\u7B97\u8FC7\u7A0B\u662F\uFF1A</p><p>\u5728\u53D1\u9001\u7AEF\uFF0C</p><p>\u2460\u628A\u4F2A\u9996\u90E8\u6DFB\u52A0\u5230<code>UDP</code>\u7528\u6237\u6570\u636E\u62A5\u4E0A\uFF1B</p><p>\u2461\u628A\u68C0\u9A8C\u548C\u5B57\u6BB5\u7F6E\u4E3A\u5168\u96F6\uFF1B</p><p>\u2462\u628A\u4F2A\u9996\u90E8\u4EE5\u53CA<code>UDP</code>\u7528\u6237\u6570\u636E\u62A5\u7684\u6240\u6709\u4F4D\u5212\u5206\u4E3A16\u4F4D\uFF082\u5B57\u8282\uFF09\u7684\u5B57\uFF0C\u82E5\u5B57\u8282\u603B\u6570\u4E0D\u662F\u5076\u6570\uFF0C\u5219\u8981\u586B\u5145\u4E00\u4E2A\u5168\u96F6\u5B57\u8282\uFF08\u6B64\u586B\u5145\u5B57\u8282\u53EA\u662F\u7528\u4E8E\u8BA1\u7B97\u68C0\u9A8C\u548C\uFF09\uFF1B</p><p>\u2463\u628A\u6240\u670916\u4F4D\u7684\u5B57\uFF0C\u6309\u4E8C\u8FDB\u5236\u53CD\u7801\u7B97\u672F\u8FD0\u7B97\u6C42\u548C\uFF1B</p><p>\u2464\u5C06\u5F97\u5230\u7684\u7ED3\u679C\u53D6\u53CD\u7801\u5199\u5165\u68C0\u9A8C\u548C\u5B57\u6BB5\uFF1B</p><p>\u2465\u628A\u4F2A\u9996\u90E8\u548C\u586B\u5145\u5B57\u8282\u4E22\u5F03\uFF1B</p><p>\u2466\u6700\u540E\u628A<code>UDP</code>\u6570\u636E\u62A5\u4EA4\u4ED8\u7ED9IP\u8F6F\u4EF6\u8FDB\u884C\u5C01\u88C5\u3002</p><p>\u5728\u63A5\u6536\u7AEF\uFF0C\u2460\u628A\u4F2A\u9996\u90E8\u52A0\u5230\u63A5\u6536\u5230\u7684<code>UDP</code>\u7528\u6237\u6570\u636E\u62A5\u4E0A\uFF1B\u2461\u82E5\u9700\u8981\uFF0C\u5E94\u6DFB\u52A0\u5168\u96F6\u7684\u586B\u5145\u5B57\u8282\uFF1B\u2462\u628A\u6240\u6709\u4F4D\u5212\u5206\u4E3A16\u4F4D\uFF082\u5B57\u8282\uFF09\u7684\u5B57\uFF1B\u2463\u628A\u6240\u670916\u4F4D\u7684\u5B57\u6309\u4E8C\u8FDB\u5236\u53CD\u7801\u7B97\u672F\u8FD0\u7B97\u6C42\u548C\uFF0C\u5E76\u53D6\u5176\u53CD\u7801\uFF1B\u2464\u82E5\u5F97\u5230\u7684\u7ED3\u679C\u662F\u51681\uFF0C\u8868\u793A\u65E0\u4F20\u8F93\u5DEE\u9519\uFF0C\u5219\u4E22\u5F03\u4F2A\u9996\u90E8\u548C\u586B\u5145\u5B57\u8282\uFF08\u5982\u6709\u7684\u8BDD\uFF09\uFF0C\u63A5\u6536\u6B64\u7528\u6237\u6570\u636E\u62A5\u3002\u5426\u5219\u5C31\u8868\u660E\u6709\u5DEE\u9519\u5B58\u5728\uFF0C\u5E94\u4E22\u5F03\u6B64\u7528\u6237\u6570\u636E\u62A5\uFF08\u4E5F\u53EF\u4E0A\u4EA4\u5E94\u7528\u5C42\uFF0C\u5E76\u9644\u4E0A\u51FA\u73B0\u5DEE\u9519\u7684\u8B66\u544A\uFF09\u3002</p><p>\u4E0B\u56FE\u8868\u793A<code>UDP</code>\u7528\u6237\u6570\u636E\u62A5\u68C0\u9A8C\u548C\u7684\u8BA1\u7B97\u4E3E\u4F8B\u3002</p><p><img src="'+c+'" alt="image"></p>',28);function r(a,s){return P}var U=d(p,[["render",r]]);export{U as default};
